mapper.insert:
INSERT INTO
teach_materials(
    teach_materials_id,file_name,course_type,category,
    uploader,file_map, create_time,update_time,validity
)
VALUES (
    #{teachMaterialsId},#{fileName},#{courseType},#{category},
    #{uploader},#{fileMap},#{createTime},#{updateTime},#{validity}
)

mapper.update:
UPDATE teach_materials
file_name=#{fileName}, course_type=#{fileName}, category=#{fileName}, file_map=#{fileName}, updateTime=#{fileName}
WHERE teach_materials_id=#{teachMaterialsId}

mapper.delete:
UPDATE teach_materials
SET validity = 0
WHERE teach_materials_id=#{teachMaterialsId}
